<?php

namespace Acme\JobeetBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * JobRespository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class JobRespository extends EntityRepository
{
	public function getActiveJobs($category_id = null, $max = null)
	{
		$qb = $this->createQueryBuilder('j')
			->where('j.expires_at > :date')
			->setParameter('date', date('Y-m-d H:i:s', time()))
			->orderBy('j.expires_at', 'DESC');

		if ($max) {
			$qb->setMaxResults($max);
		}

		if ($category_id)
		{
			$qb->andWhere('j.Category = :category_id')
				->setParameter('category_id', $category_id);
		}

		$query = $qb->getQuery();

		return $query->getResult();
	}

	public function getActiveJob($id)
	{
		$query = $this->createQueryBuilder('j')
			->where('j.id = :id')
			->setParameter('id', $id)
			->andWhere('j.expires_at > :date')
			->setParameter('date', date('Y-m-d H:i:s', time()))
			->setMaxResults(1)
			->getQuery();

		// getSingleResult devuelve una excepcion '\Doctrine\Orm\NoResultException' si no devuelve resultado
		// y '\Doctrine\Orm\NonUniqueResultException' si devuelve mÃ¡s de un resultado
		try {
			$job = $query->getSingleResult();
		} catch (\Doctrine\Orm\NoResultException $e) {
			$job = null;
		}
		return $job;
	}
}
